package com.filedemo.dto;public class BusDTO {    private Integer id;    private String busName;    private String scrq;    public Integer getId() {        return id;    }    public void setId(Integer id) {        this.id = id;    }    public String getBusName() {        return busName;    }    public void setBusName(String busName) {        this.busName = busName;    }    public String getScrq() {        return scrq;    }    public void setScrq(String scrq) {        this.scrq = scrq;    }}