package com.filedemo.service;import com.filedemo.dao.BusDAO;import com.filedemo.dao.ChanpinDAO;import com.filedemo.dto.BusDTO;import com.filedemo.dto.ChanpinDTO;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class FileService {    @Autowired    private ChanpinDAO chanpinDAO;    @Autowired    private BusDAO busDAO;    public List<ChanpinDTO> list(String search, int i, int limit) {        List<ChanpinDTO> list = chanpinDAO.list(search,i,limit);        return list;    }    public void delt(String id) {        chanpinDAO.delt(id);    }    public Integer getFileCount(Integer id) {        return chanpinDAO.getFileCount(id);    }    public void create(ChanpinDTO chanpin) {        chanpinDAO.create(chanpin);    }    public void addBus(BusDTO bus) {        busDAO.addBus(bus);    }}